import React,{useState,useEffect} from "react";
import { View, Text,FlatList,StyleSheet,ActivityIndicator,SafeAreaView,Button } from "react-native";
import {Icon } from "react-native-elements";


export default function Categories(){

    //const [data,setData]=useState([]);
    const categoriesUrl ="https://northwind.vercel.app/api/categories"
    const [name,setName]=useState([]);
    const [description,setDescription]=useState([]);
    const [id,setID]=useState([]);

    useEffect(()=>{
        getApi();
    });

    const getApi = () => {
        fetch(categoriesUrl)
        .then((response)=>response.json())
        .then((json)=>{
            setData(json);
        })
         .catch((error)=>console.error(error))   

    }


    const deleteApi=(idNumber)=>{
        let bodyContent= {
            id:idNumber
        }
        let deleteOptions = {
            method: 'DELETE',
            body:JSON.stringify(bodyContent)
        }
        fetch(categoriesUrl+id,deleteOptions)
        .then((response)=>response.json())
            .then((data) =>{

            })      
    }


    return(
        <SafeAreaView>
        <View style={styles.container}>
            <FlatList 
            data={data} keyExtractor={({id},index)=>id}
            renderItem={({item})=>(
            <View style={{paddingBottom:10}}>
            <Text style={styles.categoriesText}>
            {item.id}.{item.description},{item.name}
            <Button onPress={()=> deleteApi(item.id)}></Button>
            </Text> 

            </View>
            )} >

            </FlatList>
        </View>
        </SafeAreaView>
    );

    };

    const styles = StyleSheet.create({
        container:{
            flex:1,
            justifyContent:"center",
            backgroundColor:"#fff",

        },
        categoriesText:{
            fontSize:26,
            fontWeight:"200",
        },
    });
