import React from 'react';
import { View,Text,StyleSheet, Button } from 'react-native';
import { createStackNavigator } from 'react-navigation-stack';

export default function UpdateCategory({navigation}){

  function gotoAddCategory(id,description,name){
    navigation.navigate('AddCategory', {
    id:id,
    description:description,
    name:name,});
  }


return(
    <View>
    <Text style={styles.text2}> Details of the Selected Category </Text>          
    <Text style={styles.text}> ID: {navigation.getParam('id', 'Not Found!')}</Text>
    <Text style={styles.text}> Description: {navigation.getParam('description', 'Not Found!')}</Text>
    <Text style={styles.text}> Name: {navigation.getParam('name', 'Not Found!')}</Text>
    <Button onPress={()=>gotoAddCategory(item.id,item.description,item.name)}></Button>
  </View> 

);
};

const styles = StyleSheet.create({
    text: {
    fontFamily: 'Helvetica',
    fontSize: 20,
    color: '#191970',
    textAlign: 'center'
    },
    text2: {
    fontFamily: 'Helvetica',
    fontSize: 40,
    color: '#663399',
    fontWeight: 'bold',
    textAlign: 'center'
    }    
    });
