import React,{useState,useEffect} from "react";
import { View, Text,FlatList,StyleSheet,ActivityIndicator,SafeAreaView,Button } from "react-native";
import UpdateCategory from "./updateCategory";
import { createStackNavigator } from 'react-navigation-stack';
import { Icon } from "react-native-elements";


export default function AddCategory({navigation}){

    const [data,setData]=useState({
        name:"",
        id:"",
        description:""
    });
    const categoriesUrl ="https://northwind.vercel.app/api/categories"

    useEffect(()=>{
        postApi();
    },[]);



        const submit = async (e) => {
            await fetch("https://northwind.vercel.app/api/categories",{
              method: 'POST',
              headers: {'Content-Type': 'application/json'},
              body: JSON.stringify({
                name:data.name,
                description:data.description,
              })
            });
            navigation.state.params.context.getApi()
            navigation.navigate('Categories')
          }

   function handle(e){
       const newData = {...data}
       newData[e.target.id] = e.target.value
       setData(newData)

   }

    return(
        <SafeAreaView>
            <FlatList>
              <View>       
       <Input type="text" onChange={(e)=>handle(e)} name="id" value={data.id}> </Input>
       <Input type="text" onChange={(e)=>handle(e)} name="description" value={data.description}> </Input>
       <Input type="text" onChange={(e)=>handle(e)} name="name" value={data.name}> </Input>
       <Button color='#517fa4' onPress={()=>postApi()}>Add New Category</Button>
               </View> 


            </FlatList>

        </SafeAreaView>
    );

    };

    const styles = StyleSheet.create({
        container:{
            flex:1,
            justifyContent:"center",
            backgroundColor:"#fff",

        },
        categoriesText:{
            fontSize:26,
            fontWeight:"200",
        },
        deleteButton:{
            color:"blue",
            width: "100px",
            height:"100px",

        },
        addButton:{
            color:"red",
            width: "100px",
            height:"100px",
        },
        updateButton:{
            color:"black",
            width: "100px",
            height:"100px",
        },
    });
