import React,{useState,useEffect} from "react";
import { View, Text,FlatList,StyleSheet,ActivityIndicator,SafeAreaView,Button } from "react-native";
import UpdateCategory from "./updateCategory";
import { createStackNavigator } from 'react-navigation-stack';
import { Icon } from "react-native-elements";


export default function AddCategory({navigation}){

    const [data,setData]=useState([]);
    const categoriesUrl ="https://northwind.vercel.app/api/categories"

    useEffect(()=>{
        postApi();
    },[]);

    const postApi=()=>{

        fetch("https://northwind.vercel.app/api/categories",{
            method: 'POST'
        })
        .then((response)=>response.json())
    }


    return(
        <SafeAreaView>
            <FlatList>
              <View>       
        <Text style={styles.text}> ID: {navigation.getParam('id', 'Not Found!')}</Text>
        <Text style={styles.text}> Description: {navigation.getParam('description', 'Not Found!')}</Text>
        <Text style={styles.text}> Name: {navigation.getParam('name', 'Not Found!')}</Text>
               </View> 

            <Button color='#517fa4' onPress={()=>postApi()}>Add New Category</Button>

            </FlatList>

        </SafeAreaView>
    );

    };

    const styles = StyleSheet.create({
        container:{
            flex:1,
            justifyContent:"center",
            backgroundColor:"#fff",

        },
        categoriesText:{
            fontSize:26,
            fontWeight:"200",
        },
        deleteButton:{
            color:"blue",
            width: "100px",
            height:"100px",

        },
        addButton:{
            color:"red",
            width: "100px",
            height:"100px",
        },
        updateButton:{
            color:"black",
            width: "100px",
            height:"100px",
        },
    });
